import React, { useState } from 'react';

function Clicker() {
  const [count, setCount] = useState(0);
  const [imgData, setImgData] = useState({ show: false, x: 0, y: 0 });

  // Функция для генерации случайной позиции
  const getRandomPosition = () => {
    const x = Math.floor(Math.random() * (window.innerWidth - 100));
    const y = Math.floor(Math.random() * (window.innerHeight - 100));
    return { x, y };
  };

  const handleClick = () => {
    setCount(prev => prev + 1);
    const { x, y } = getRandomPosition();
    setImgData({ show: true, x, y });
    setTimeout(() => {
      setImgData(prev => ({ ...prev, show: false }));
    }, 2000);
  };

  // Путь к картинке, которая лежит в public/image.jpg
  const imageSrc = '/image.jpg';

  const imgStyle = {
    position: 'absolute',
    left: imgData.x,
    top: imgData.y,
    transition: 'opacity 0.3s'
  };

  const containerStyle = {
    position: 'relative',
    minHeight: 'calc(100vh - 60px)', // учитываем высоту шапки
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center'
  };

  return (
    <div style={containerStyle}>
      <button onClick={handleClick} style={{ padding: '10px 20px', fontSize: '16px' }}>
        Кликни меня!
      </button>
      <p style={{ fontSize: '20px', marginTop: '10px' }}>{count}</p>
      {imgData.show && (
        <img src={imageSrc} alt="Random" style={imgStyle} />
      )}
    </div>
  );
}

export default Clicker;
