import React, { useState } from 'react';
import './Clicker.css';

function Clicker() {
  const [count, setCount] = useState(0);
  const [imgData, setImgData] = useState({ show: false, x: 0, y: 0 });

  const getRandomPosition = () => {
    const x = Math.floor(Math.random() * (window.innerWidth - 222));
    const y = Math.floor(Math.random() * (window.innerHeight - 100));
    return { x, y };
  };

  const handleClick = () => {
    setCount(prev => prev + 1);
    const { x, y } = getRandomPosition();
    setImgData({ show: true, x, y });
    setTimeout(() => {
      setImgData(prev => ({ ...prev, show: false }));
    }, 2000);
  };

  // Используем process.env.PUBLIC_URL для корректного пути к картинке из public
  const imageSrc = process.env.PUBLIC_URL + '/ej.jpg';

  return (
    <div className="clicker-container">
      <button className="clicker-button" onClick={handleClick}>
        Кликни меня!
      </button>
      <p className="clicker-count">{count}</p>
      {imgData.show && (
        <img
          src={imageSrc}
          alt="Random"
          style={{
            position: 'absolute',
            left: imgData.x,
            top: imgData.y,
            transition: 'opacity 0.3s'
          }}
        />
      )}
    </div>
  );
}

export default Clicker;
